 */classJI_Controllerextends\SENE_Controller{protected$user_login=false;protected$admin_login=false;private$session_current=null;publicfunction__construct(){parent::__construct();$this->session_current_check();
    }publicfunctionsession_current_check(){$session=$this->session_current;if(!is_null($session) &&isset($session->user->id) &&isset($session->admin->id)) {return$session;
        }$session=$this->getKey();if(!is_object($session)) {$session=newstdClass();$session->user =newstdClass();$session->admin =newstdClass();
        }if(!isset($session->user)) {$session->user =newstdClass();
