        }$this->session_current =$session;return$this;
    }protectedfunctionconfig_semevar($key,$default=''){if(isset($this->config->semevar->{$key})) {return$this->config->semevar->{$key};
        }else{return$default;
        }
    }publicfunction__init(){$data=array();$data['sess'] =$this->session_current;return$data;
    }privatefunction__json_response_default($data){$data=array();$data["status"]  = (int)$this->status;$data["message"] =$this->message;$data["data"]  =$data;return$data;
    }protectedfunctionresponse_missing_required_parameter(){$this->status =422;$this->message ='One or more parameter was missing';
        header("HTTP/1.0 422 missing some required parameter(s)");$this->__json_out(array());die();
    }protectedfunctionresponse_no_data(){$this->status =204;$this->message ='No data';
        header("HTTP/1.0 204 no data");$this->__json_out(array());die();
    }protectedfunctionvalidate_id($id){$id= intval($id);if($id<=0) {$id=0;
        }if(empty($id)) {$this->response_missing_required_parameter();
        }return$id;
    }/**
     * API need to protected from unauthorized access
     *
     *@parammixed $dt input object or array
     *@returnstring     sting json formatted with its header
     */publicfunction__protect_api($data=array()){if(!$this->admin_login) {$this->status =400;$this->message ='Harus login';
            header("HTTP/1.0 400 Harus login");$this->__json_out($data);die();
        }
    }privatefunctionsorting_direction(){$sorting_direction= strtoupper($this->input->post("sSortDir_0",'DESC'));if($sorting_direction!="ASC") {return'DESC';
        }else{return'ASC';
        }
    }privatefunctionsorting_column(){returntrim(strtoupper($this->input->post("iSortCol_0",'id')));
    }privatefunctionfilter_keyword(){returntrim(strip_tags($this->input->post("sSearch",'')));
    }/**
