     *@returnobject     datatable request object
     */publicfunction__datatable_request(){$datatable_request=newstdClass();$datatable_request->sorting =newstdClass();$datatable_request->sorting->column =$this->sorting_column();$datatable_request->sorting->direction =$this->sorting_direction();$datatable_request->filter =newstdClass();$datatable_request->filter->keyword =$this->filter_keyword();$datatable_request->pagination =newstdClass();$datatable_request->pagination->page =$this->input->post("iDisplayStart");$datatable_request->pagination->size = intval($this->input->post("iDisplayLength",0));return$datatable_request;
    }/**
     * Procedure for output the json formatted string
     *   to HTTP Response
     *
     *@parammixed $dt input object or array
     *@returnstring     sting json formatted with its header
     */publicfunction__json_out($data){$this->lib('sene_json_engine','sene_json');$this->sene_json->out($this->__json_response_default($data));die();
    }/**
     * Procedure for generating output for datatable response
     *   on JSON format
     * 
     * This can be consumed for DataTable Ajax request API
     *
     *@paramarray $data    array of object
     *@paramint   $count   number of counted row
     *@paramarray $another array of array for addition information
     * 
     *@returnDatatable array of array response
     */publicfunction__json__datatable($data,$count,$another=array()){$this->lib('sene_json_engine','sene_json');$rdata=array();if(!is_array($data)) {$data=array();
        }$dt1=array();$dt2=array();if(!is_array($data)) {
            trigger_error('jsonDataTable first params need array!');die();
        }foreach($dataas$dat) {$dt2=array();if(is_int($dat)) {
                trigger_error('[ERROR: '.$dat.'] Data table not well performed because a query execution error!');
            }foreach($datas$dt) {$dt2[] =$dt;
            }$dt1[] =$dt2;
        }if(is_array($another)) {$rdata=$another;
        }$rdata['data'] =$dt1;$rdata['recordsFiltered'] =$count;$rdata['recordsTotal'] =$count;$rdata['status'] = (int)$this->status;$rdata['message'] =$this->message;$this->sene_json->out($rdata);die();
    }publicfunctionindex(){ }
}
