     *@paramarray   $d   Contain associative array that represent the pair of column and value
     *@returnint          Return last ID if succeed, otherwise will return 0
     */publicfunctionset($d){$this->db->insert($this->tbl,$d,0,0);return$this->db->last_id;
    }/**
     * Update a row data by supplied ID
     *
     *@paramint      $id    Positive integer
     *@returnboolean         True if succeed, otherwise false
     */publicfunctionupdate($id,$d){$this->db->where("id",$id);return$this->db->update($this->tbl,$d,0);
    }/**
     * Delete row data by ID
     *
     *@paramint      $id    Positive integer
     *@returnboolean         True if succeed, otherwise false
     */publicfunctiondel($id){$this->db->where("id",$id);return$this->db->delete($this->tbl);
    }/**
     * Get single row data by ID
     *
     *@paramint      $id     Positive integer
     *@returnstdClass         Will return single row object, otherwise will return empty object
     */publicfunctionid($id){$this->db->where("id",$id);return$this->db->from($this->tbl,$this->tbl_as)->get_first('',0);
    }/**
     * Open the database transaction session
     *@returnboolean True if succeed, otherwise false
     */publicfunctiontrans_start(){$r=$this->db->autocommit(0);if($r) {return$this->db->begin();
        }returnfalse;
    }/**
     * Execute `commit` SQL command
     *@returnboolean True if succeed, otherwise false
     */publicfunctiontrans_commit(){return$this->db->commit();
    }/**
     * Rollback the database transaction session
     *@returnboolean True if succeed, otherwise false
     */publicfunctiontrans_rollback(){return$this->db->rollback();
    }/**
     * Finalize the database transaction session
     *@returnboolean True if succeed, otherwise false
     */publicfunctiontrans_end(){return$this->db->autocommit(1);
    }
}
