<?php/**
* Main Controller Class for Perusahaan Master Data
*   on PoV API Admin
*
* Mostly for this controller will resulting HTTP Body Content in HTML format
*
*@version1.0.0
*
*@packageController\Admin\API\Perusahaan
*@since1.0.0
*/classMasterDataextends\JI_Controller{publicfunction__construct(){parent::__construct();$this->lib("seme_purifier");$this->load("a_company_concern");$this->load("api_admin/a_company_model",'acm');
    }/**
     * Mapping the column number from datatable request into column name
     *
     *@paramint     $column_number
     * 
     *@returnstring  Full qualified column name with table prefix
     */privatefunctionsorting_column($column_number){// define all table aliases here$tbl_as=$this->acm->tbl_as;// translate number column from datatable request to//   column nameswitch($column_number) {case0:$sorting_column="$tbl_as.id";break;case1:$sorting_column="$tbl_as.utype";break;case2:$sorting_column="$tbl_as.kode";break;case3:$sorting_column="$tbl_as.nama";break;case4:$sorting_column="concat($tbl_as.kabkota, ' ',$tbl_as.provinsi)";break;case5:$sorting_column="$tbl_as.telp";break;case6:$sorting_column="$tbl_as.is_active";break;default:$sorting_column="$tbl_as.id";
        }return$sorting_column;
    }publicfunctionindex(){$init_data=$this->__init();$this->__protect_api();$request=$this->__datatable_request();$utype=$this->input->post("utype",'');$is_active=$this->input->post("is_active",'');$this->status =200;$this->message ='Berhasil';$result_data=$this->acm->data($request->pagination->page,$request->pagination->size,$this->sorting_column($request->sorting->column),$request->sorting->direction,$request->filter->keyword,$utype,$is_active);$result_count=$this->acm->count($request->filter->keyword,$utype,$is_active);foreach($result_dataas&$gd){if(isset($gd->nama)) {$gd->nama = htmlentities(rtrim($gd->nama,' - '));
            }if(isset($gd->utype)) {$gd->utype = ucfirst($gd->utype);
            }if(isset($gd->alamat)) {$gd->alamat = htmlentities(ltrim($gd->alamat,', '));
            }if(isset($gd->is_active)) {if(!empty($gd->is_active)) {$gd->is_active ='<label class="label label-success">Aktif</label>';
                }else{$gd->is_active ='<label class="label label-default">Tidak Aktif</label>';
                }
            }
        }$this->__json__datatable($result_data,$result_count);
    }privatefunctionvalidate_input(){$data_to_insert=$_POST;foreach($data_to_insertas&$di){$di= trim(strip_tags($di));
        }if(!isset($data_to_insert['nama'])) {$this->response_missing_required_parameter();
        }return$data_to_insert;
    }publicfunctionbaru(){$init_data=$this->__init();$this->__protect_api();$this->acm->trans_start();$res=$this->acm->set($this->validate_input());if($res) {$this->acm->trans_commit();$this->status =200;$this->message ='Data baru berhasil ditambahkan';

        }else{$this->status =901;$this->message ='Tidak dapat menyimpan data ke database';$this->acm->trans_rollback();
        }$this->acm->trans_end();$this->__json_out(array());
    }publicfunctiondetail($id){$init_data=$this->__init();$this->__protect_api();$data=$this->acm->id($this->validate_id($id));if(!isset($data->id)) {$this->response_no_data();
        }$this->status =200;$this->message ='Berhasil';$this->__json_out($data);
    }publicfunctionedit($id){$init_data=$this->__init();$this->__protect_api();$acm=$this->acm->id($this->validate_id($id));if(!isset($acm->id)) {$this->response_no_data();
        }$res=$this->acm->update($id,$this->validate_input());if($res) {$this->status =200;$this->message ='Success';
        }else{$this->status =901;$this->message ='Tidak dapat merubah data ke database';
        }$this->__json_out(array());
    }publicfunctionhapus($id){$init_data=$this->__init();$this->__protect_api();$acm=$this->acm->id($this->validate_id($id));if(!isset($acm->id)) {$this->response_no_data();
        }$res=$this->acm->del($id);if($res) {$this->status =200;$this->message ='Berhasil';
        }else{$this->status =902;$this->message ='Tidak dapat menghapus data ke database';
        }$this->__json_out(array());
    }

}
