		$sess = $this->getKey();
		if(!isset($sess->user_login)) $sess->user_login = 1;
		if(!isset($sess->user)) $sess->user = new stdClass();
		if(!isset($sess->user->id)) $sess->user->id = 1000;
		$this->setKey($sess);
	}
	public function set_logged_out(){
		$sess = $this->getKey();
		if(!isset($sess->user_login)) $sess->user_login = 0;
		if(!isset($sess->user)) $sess->user = new stdClass();
		if(!isset($sess->user->id)) $sess->user->id = 1000;
		unset($sess->user->id);
		$this->setKey($sess);
	}
}

